INSERT INTO News (id, date_new, img_new, text_img, title, description) VALUES (1, '2025-05-12', 'https://i0.wp.com/www.tomosygrapas.com/wp-content/uploads/2025/05/Banner-Pacificador.jpg?resize=1024%2C576&ssl=1', 'Noticia sobre el primer trailer de la serie del pacificador', 'Vuelve Pacificador con el primer tráiler de su segunda temporada', 'La plataforma de streaming Max lanza el primer avance del regreso de Pacificador con el teaser tráiler de la segunda temporada de su serie.');
INSERT INTO News (id, date_new, img_new, text_img, title, description) VALUES (2, '2025-05-06', 'https://i0.wp.com/www.tomosygrapas.com/wp-content/uploads/2025/05/Star-Wars-Banner.jpg?w=999&ssl=1', 'Star Wars decide pasarse al terror', 'Star Wars se pasa al terror con una nueva serie', 'Dark Horse se prepara para darle un nuevo enfoque a Star Wars con una nueva serie de género de terror.');
INSERT INTO News (id, date_new, img_new, text_img, title, description) VALUES (3, '2025-05-05', 'https://i0.wp.com/www.tomosygrapas.com/wp-content/uploads/2025/05/predator-banner.jpg?w=1012&ssl=1', 'Predator se enfrentará a los superhéroes de Marvel', 'Predator se enfrenta a todo el Universo Marvel', 'Marvel Comics anuncia un nuevo crossover donde el icónico Predator de la franquicia cinematográfica, se enfrentará a todo el Universo Marvel.');
INSERT INTO News (id, date_new, img_new, text_img, title, description) VALUES (4, '2025-05-15', 'https://i0.wp.com/www.tomosygrapas.com/wp-content/uploads/2025/05/Banner-Superman.jpg?resize=1024%2C576&ssl=1', 'Nuevo trailer de Superman', 'Superman se luce como nunca en su nuevo tráiler', 'DC Studios lanza un nuevo tráiler de la próxima película de Superman de James Gunn, protagonizado por David Corenswet y Rachel Brosnahan.');
INSERT INTO News (id, date_new, img_new, text_img, title, description) VALUES (5, '2025-05-16', 'https://i0.wp.com/www.tomosygrapas.com/wp-content/uploads/2025/05/Wolverine-Banner.jpg?w=1012&ssl=1', 'Lobezno luchando con samurais', 'Marvel recupera una historia de Lobezno de Claremont', 'Marvel va a publicar este verano un especial de Chris Claremont y Tom Reilly protagonizado por Lobezno, y que está ubicado en el pasado del personaje, en concreto en su etapa en Japón que vimos en la miniserie Lobezno: Honor.');
INSERT INTO News (id, date_new, img_new, text_img, title, description) VALUES (6, '2025-05-19', 'https://i0.wp.com/www.tomosygrapas.com/wp-content/uploads/2025/05/Marvel-All-In-One-banner.jpg?w=1001&ssl=1', 'La cosa luchando contra los vengadores', 'La Cosa contra todo el Universo Marvel', 'La Cosa se enfrentará a todos los héroes del Universo Marvel en un especial firmado por Ryan North y Ed McGuinness, y que estará compuesto casi en su totalidad por páginas dobles.');
